insert  into `command`(`name`,`security`,`help`) values ('guild invite',6,'Syntax: .guild invite [$CharacterName] \"$GuildName\"\r\n\r\nAdd player $CharacterName (or selected) into a guild $GuildName. Guild name must in quotes.');
insert  into `command`(`name`,`security`,`help`) values ('guild rank',6,'Syntax: .guild rank $CharacterName #Rank\r\n\r\nSet for $CharacterName rank #Rank in a guild.');
insert  into `command`(`name`,`security`,`help`) values ('guild uninvite',6,'Syntax: .guild uninvite [$CharacterName]\r\n\r\nRemove player $CharacterName (or selected) from a guild.');
